package com;

public class PrimitiveDatatypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		
		
		//we are storing whole number values
		
		byte box1=120; //-121 till 120
		

		short box2=100;

		int box3=1000;

		Long box4=10000;
		
	
		
		
		
		//we are storing floating numbers 
		float f=12.99f;
		
	
		double d=12.95;
		
		//we are storing single character values 
		
		char a='*';
		char b='A';
		char c='1';
		
		//to store boolean values
		
		boolean variabel1=true;
		
		boolean variabel2=false;
		
		//I would like to store value 99
		
		Int number=99;
		
		System.out.println(d);//12.95
		System.out.println(a);//*
		
			
	}

}
