package com.Class07;

public class PracticeLoops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num=1;
		
		while (num>=1&&num<=100) {
			System.out.print(num+", ");
			num++;
			
		}
		System.out.println();
		
		int num1=100;
		
		while (num1>=1&&num1<=100) {
			System.out.print(num1+", ");
			num1--;
		}
		System.out.println();
		
		int num2=20;
		
		while (num2>=1&&num2<=100) {
			System.out.print(num2+", ");
			num2+=2;
		}
	}
	
	
		

}
