package com.NewJava04;

public class ReplaceAll {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String var="Syntax is best 12546557";
		System.out.println(var.replaceAll("[0-7]", ""));
			var="626984751hjkajh";	
		System.out.println(var.replaceAll("[a-zA-Z]", ""));
		var="3215ikjhgaseAHJHGDfygv*&^#%";
		System.out.println(var.replaceAll("[^A-Z]", ""));
		
		
		
		
	}

}
